import css from 'styled-jsx/css'

export default css.global`
  body {
    font-size: 16px;
    background: white;
    font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
    overflow-x: hidden;
    z-index: 1;
  }

  header{
    font-size: 1em;
    font-weight: 400;
    margin-right: 50em;
  }

  .header-section{
    min-height:100vh;
    background: url("/static/headerimage.jpg");
    background-size: cover;
    width: 100%;
  }
  .intro-text{
    font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
    font-size: 43px;
    text-align: left;
    line-height: 80%;
  }
  nav{
    position:fixed;
    right: 0;
    z-index: 997;
  }

  nav li{
    display: inline-block;
  }
  .nav-link{
    cursor: pointer;
    text-decoration: none;
    color: black;
    padding:1em;
  }
  .about-container{
    min-height:100vh;
    background:white;
    background-size: cover;
    display: grid;
    grid-template-columns: auto 500px;
  }
  .about-description{
    font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
    font-size: 20px;
    text-align: left;
    display: inline-block;
  }
  .about-image{
      display: inline-block;
      width: 500px;
      margin-top: 40px;
  }
  .list-inline-icons{

  }
  .list-inline-item{
      display: inline-block;
      padding: 1em;
  }
`
