import css from 'styled-jsx/css'

export default css.global`
  body {
    font-size: 16px;
    background: white;
    font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
    overflow-x: hidden;
    z-index: 1;
  }
  .App {
    text-align: center;
  }
  header{
    font-size: 1em;
    font-weight: 400;
    margin-right: 50em;
  }

  .header-text{
      margin-top:3em;
  }

  .intro-text{
    position: relative;
    font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
    font-size: 3em;
    text-align: left;
    top:2em;
  }
  nav{
    position:fixed;
    left:60em;
    top:6em;
  z-index: 2;
  }
  nav li{
    display: inline-block;
    cursor: pointer;
    text-decoration: none;
    color: black;
    padding:1em;
    opacity: 0.5;
    transition: all 0.3s ease-in-out;
    -webkit-transition: all 0.3s ease-in-out;
    -moz-transition: all 0.3s ease-in-out;
    -ms-transition: all 0.3s ease-in-out;
    -o-transition: all 0.3s ease-in-out;
  }
  .about-container{
      margin-top: 20px;
  }
  .about-content{
      position: relative;
      left:15em;
  }
  .about-content h1{
    font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
    font-size: 5em;
    text-align: left;
  }
  .about-content p{
    font-size: 1.3em;
    text-align: left;
    width: 37em;
  }
  .about-image{
      margin-left:5em;
      width:500px;
  }
`
