import css from 'styled-jsx/css'

export default css.global`
  body {
    font-size: 16px;
    background: white;
    font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
    overflow-x: hidden;
    z-index: 1;
  }

  header{
    font-size: 1em;
    font-weight: 400;
    margin-right: 50em;
  }

  .header-section{
    min-height:100vh;
    background: url("/static/headerimage.jpg");
    background-size: cover;
    width: 100%;
  }
  .intro-text{
    font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
    font-size: 43px;
    text-align: left;
    line-height: 80%;
  }
  nav{
    position:fixed;
    right: 0;
    z-index: 997;
  }

  nav li{
    display: inline-block;
  }
  .nav-link{
    cursor: pointer;
    text-decoration: none;
    color: black;
    opacity: .5;
    padding:1em;
  }
  .nav-link:hover{
    opacity: 1;
  }
  .about-container{
    min-height:100vh;
    background:white;
    background-size: cover;
  }
  .about-content{
    display: grid;
    grid-template-columns: auto 500px;
  }
  .about-description{
    font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
    font-size: 20px;
    text-align: left;
    display: inline-block;
  }
  .about-image{
      display: inline-block;
      width: 500px;
      margin-top: 30px;
  }
  .icon-horizontal-list li{
    display: inline-block;
    margin-right: 5px;
  }
  .icon-horizontal-list li:hover{
    color: #0e4ba8;
  }
  .additional-skills-list{
    font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
    font-size: 20px;
    list-style: none;
  }
  .projects-section{
    background: url("/static/projectsimage.jpg");
    background-size: cover;
    width: 100%;
  }
  .projects-container{
    display: grid;
    grid-template-columns: 50% 50%;
    grid-gap: 10px;
  }
  .projects-card{
      padding: 10px 10px;
      margin: 15px 15px;
      background-color: white;
      border-radius:8px;
  }
  .project-image{
      object-fit:cover;
      width: 98%;
  }
  .fa-github:hover{
    color: #0e4ba8;
  }
  .fa-link:hover{
    color: #0e4ba8;
  }
  .contact-section{
    background: url("/static/contactimage.jpg");
    background-size: cover;
    width: 100%;
  }
`
