import css from 'styled-jsx/css'

export default css.global`
  body {
    font-size: 16px;
    background: white;
    font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
    overflow-x: hidden;
    z-index: 1;
  }
  .App {
    text-align: center;
  }
  header{
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
  }
  header h1{
    font-size: 1em;
    font-weight: 400;  
    padding: 2em;
    margin-right: 50em;
  }
  .intro-text{
    position: relative;
    font-family: 'Montserrat', sans-serif;
    font-size: 3em;
    text-align: left;
    top:2em;
  }
`
